mapper.insert:
{CALL registrarCliente( #{cliente.nombre, mode=IN, jdbcType=VARCHAR}, #{cliente.apellidoPaterno, mode=IN, jdbcType=VARCHAR}, #{cliente.apellidoMaterno, mode=IN, jdbcType=VARCHAR}, #{cliente.telefono, mode=IN, jdbcType=VARCHAR}, #{cliente.correo, mode=IN, jdbcType=VARCHAR},
#{direccion.calle, mode=IN, jdbcType=VARCHAR}, #{direccion.numero, mode=IN, jdbcType=INTEGER}, #{direccion.colonia, mode=IN, jdbcType=VARCHAR}, #{direccion.codigoPostal, mode=IN, jdbcType=VARCHAR}, #{direccion.idMunicipio, mode=IN, jdbcType=INTEGER},
#{filasAfectadas, mode=INOUT, jdbcType=INTEGER}, #{error, mode=INOUT, jdbcType=VARCHAR} )}

mapper.delete:
DELETE FROM cliente
WHERE idCliente = #{idCliente}
AND NOT EXISTS (
    SELECT 1
    FROM envio
    WHERE idCliente = #{idCliente}
)
